import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ClienteService } from '../../services/cliente.service';
import { Cliente } from '../../models/cliente.model';

@Component({
  selector: 'app-clientes',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="container-fluid">
      <div class="row mb-4">
        <div class="col-12">
          <h1 class="h3 mb-0">Gestión de Clientes</h1>
          <p class="text-muted">Administra la información de los clientes</p>
        </div>
      </div>

      <!-- Formulario -->
      <div class="row mb-4">
        <div class="col-12">
          <div class="card">
            <div class="card-header">
              <h5 class="mb-0">{{editando ? 'Editar' : 'Registrar'}} Cliente</h5>
            </div>
            <div class="card-body">
              <form (ngSubmit)="guardarCliente()" #clienteForm="ngForm">
                <div class="row">
                  <div class="col-md-6 mb-3">
                    <label for="cedula" class="form-label">Cédula</label>
                    <input type="text" class="form-control" id="cedula" 
                           [(ngModel)]="cliente.cedula" name="cedula" 
                           [readonly]="editando" required maxlength="10">
                  </div>
                  <div class="col-md-6 mb-3">
                    <label for="nombre" class="form-label">Nombre</label>
                    <input type="text" class="form-control" id="nombre" 
                           [(ngModel)]="cliente.nombre" name="nombre" required maxlength="50">
                  </div>
                </div>
                <div class="row">
                  <div class="col-md-6 mb-3">
                    <label for="apellido" class="form-label">Apellido</label>
                    <input type="text" class="form-control" id="apellido" 
                           [(ngModel)]="cliente.apellido" name="apellido" required maxlength="50">
                  </div>
                  <div class="col-md-6 mb-3">
                    <label for="telefono" class="form-label">Teléfono</label>
                    <input type="text" class="form-control" id="telefono" 
                           [(ngModel)]="cliente.telefono" name="telefono" required maxlength="15">
                  </div>
                </div>
                <div class="row">
                  <div class="col-12 mb-3">
                    <label for="direccion" class="form-label">Dirección</label>
                    <input type="text" class="form-control" id="direccion" 
                           [(ngModel)]="cliente.direccion" name="direccion" required maxlength="50">
                  </div>
                </div>
                <div class="d-flex gap-2">
                  <button type="submit" class="btn btn-primary" [disabled]="!clienteForm.form.valid">
                    <i class="fas fa-save me-2"></i>{{editando ? 'Actualizar' : 'Guardar'}}
                  </button>
                  <button type="button" class="btn btn-secondary" (click)="cancelarEdicion()">
                    <i class="fas fa-times me-2"></i>Cancelar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>

      <!-- Lista de Clientes -->
      <div class="row">
        <div class="col-12">
          <div class="card">
            <div class="card-header">
              <h5 class="mb-0">Lista de Clientes</h5>
            </div>
            <div class="card-body">
              <div *ngIf="cargando" class="text-center">
                <div class="spinner-border" role="status">
                  <span class="visually-hidden">Cargando...</span>
                </div>
              </div>
              <div *ngIf="!cargando && clientes.length === 0" class="text-center text-muted">
                No hay clientes registrados
              </div>
              <div *ngIf="!cargando && clientes.length > 0" class="table-responsive">
                <table class="table table-hover">
                  <thead>
                    <tr>
                      <th>Cédula</th>
                      <th>Nombre</th>
                      <th>Apellido</th>
                      <th>Teléfono</th>
                      <th>Dirección</th>
                      <th>Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr *ngFor="let cliente of clientes">
                      <td>{{cliente.cedula}}</td>
                      <td>{{cliente.nombre}}</td>
                      <td>{{cliente.apellido}}</td>
                      <td>{{cliente.telefono}}</td>
                      <td>{{cliente.direccion}}</td>
                      <td>
                        <div class="btn-group btn-group-sm">
                          <button class="btn btn-warning" (click)="editarCliente(cliente)">
                            <i class="fas fa-edit"></i>
                          </button>
                          <button class="btn btn-danger" (click)="eliminarCliente(cliente.cedula)">
                            <i class="fas fa-trash"></i>
                          </button>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Alertas -->
      <div *ngIf="mensaje" class="alert" [ngClass]="{'alert-success': !error, 'alert-danger': error}" 
           role="alert">
        {{mensaje}}
      </div>
    </div>
  `,
  styles: []
})
export class ClientesComponent implements OnInit {
  clientes: Cliente[] = [];

  cliente: Cliente = {
    cedula: '',
    nombre: '',
    apellido: '',
    direccion: '',
    telefono: ''
  };

  editando = false;
  cargando = false;
  mensaje = '';
  error = false;

  constructor(private clienteService: ClienteService) { }

  ngOnInit(): void {
    this.cargarClientes();
  }

  cargarClientes(): void {
    this.cargando = true;
    this.clienteService.obtenerClientes().subscribe({
      next: (data) => {
        this.clientes = data;
        this.cargando = false;
      },
      error: (error) => {
        this.mostrarMensaje('Error al cargar clientes', true);
        this.cargando = false;
      }
    });
  }

  guardarCliente(): void {
    if (this.editando) {
      this.clienteService.actualizarCliente(this.cliente.cedula, this.cliente).subscribe({
        next: () => {
          this.mostrarMensaje('Cliente actualizado exitosamente');
          this.cargarClientes();
          this.cancelarEdicion();
        },
        error: (error) => this.mostrarMensaje('Error al actualizar cliente', true)
      });
    } else {
      this.clienteService.guardarCliente(this.cliente).subscribe({
        next: () => {
          this.mostrarMensaje('Cliente registrado exitosamente');
          this.cargarClientes();
          this.limpiarFormulario();
        },
        error: (error) => this.mostrarMensaje('Error al registrar cliente', true)
      });
    }
  }

  editarCliente(cliente: Cliente): void {
    this.cliente = { ...cliente };
    this.editando = true;
  }

  eliminarCliente(cedula: string): void {
    if (confirm('¿Está seguro de eliminar este cliente?')) {
      this.clienteService.eliminarCliente(cedula).subscribe({
        next: () => {
          this.mostrarMensaje('Cliente eliminado exitosamente');
          this.cargarClientes();
        },
        error: (error) => this.mostrarMensaje('Error al eliminar cliente', true)
      });
    }
  }

  cancelarEdicion(): void {
    this.editando = false;
    this.limpiarFormulario();
  }

  limpiarFormulario(): void {
    this.cliente = {
      cedula: '',
      nombre: '',
      apellido: '',
      direccion: '',
      telefono: ''
    };
  }

  mostrarMensaje(mensaje: string, esError = false): void {
    this.mensaje = mensaje;
    this.error = esError;
    setTimeout(() => this.mensaje = '', 5000);
  }
}
